
public class Ex_20221229_06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub	
		int i = 0;
		System.out.println(i);
		System.out.println(i++);
		System.out.println(i);
		/*
			print(i++)  ==  print(i)
			                i = i+1
		*/
		i=0;
		System.out.println(i);
		System.out.println(++i);
		System.out.println(i);
		/*
		print(++i)  == i = i+1
		               print(i)
		                
		 */
		
	}

}
